import { evaluate } from "mathjs";

const appendReducer = (state = "", action) => {
    switch(action.type) {
        case "APPEND_NUM":
            return state + action.char.toString()
        case "BACKSPACE_NUM":
            return state.slice(0, -1);
        default:
            return state;
    }
}
export default appendReducer;
