import { evaluate } from "mathjs";

const appendReducer = (state = "", action) => {
    switch(action.type) {
        case "APPEND_NUM":
            let last = state.charAt(state.length);
            if (parseFloat(last) >= 0 || state.length === 0 ) {
                return state + action.char.toString();
            }
            return state + action.char.toString() + " ";
        case "BACKSPACE_NUM":
            return state.toString().slice(0, -2);
        case "EQUALS":
            return evaluate(state);
        case "SQRT":
            return "sqrt(" + state.toString() + ")";
        case "CLEAR":
            return "";
        default:
            return state;
    }
}
export default appendReducer;
