import { evaluate } from "mathjs";

const appendReducer = (state = "", action) => {
    switch(action.type) {
        case "APPEND_NUM":
            return state + action.char.toString()
        case "BACKSPACE_NUM":
            return state.toString().slice(0, -1);
        case "EQUALS":
            return evaluate(state);
        case "SQRT":
            return "sqrt(" + state.toString() + ")";
        default:
            return state;
    }
}
export default appendReducer;
